mapper.insert:
insert into course
	(USER_ID, TILE_CODE, COURS_PASNG_TIME)
values (#{user_id},
		(select t.TILE_CODE
			from tile t, beacon b
			where b.BEACON_MJR = #{beacon_mjr}
			and b.BEACON_MNR = #{beacon_mnr}
			and b.BEACON_CODE = t.BEACON_CODE) ,
		#{cours_pasng_time} );

mapper.select:
select C.tile_visit, T.total_visit, (C.tile_visit / T.total_visit) AS probability
from ( select count(DISTINCT user_id) AS tile_visit from course where TILE_CODE = ( select TILE_CODE from tile where DRW_CODE = #{drw_code} and TILE_CRDNT_X = #{tile_crdnt_x} and TILE_CRDNT_Y = #{tile_crdnt_y} ) and date(COURS_PASNG_TIME) >= date(subdate(CURDATE(), INTERVAL 0 DAY)) ) C,
( select count(DISTINCT user_id) AS total_visit from course where date(COURS_PASNG_TIME) >= date(subdate(curDate(), INTERVAL 0 DAY)) ) T
